'use strict';

angular.module('trinity')
    .controller('aggregateRootsCtrl', ['$rootScope', '$scope', '$log', 'User'
        , function ($rootScope, $scope, $log, User){
            $scope.searchAggregateBoot = '';
            $scope.detailData = '';
            $scope.aggregateRoots = [];

            $scope.init = function() {
                CQRS.fetchAggregateRoots().then(function(resp) {
                    $scope.aggregateRoots = angular.fromJson(resp.data);
                });
            };

            $scope.showData = function (data) {
                $scope.detailData = data;
            }

            return $scope.init();
        }]);
